async ({
  operationFolderPath,
}: {
  operationFolderPath: string;
}) => {
  const problems: string[] = [];

  const packageJson = await getPackageJson({ operationFolderPath });
  if (!packageJson) {
    problems.push("package.json should exist");
  } else {
    if (!packageJson.name) {
      problems.push("package.json needs a name");
    }
    if (!packageJson.version) {
      problems.push("package.json needs a version");
    }
  }

  const tsConfig = await getTsConfig(operationFolderPath);
  if (!tsConfig) {
    problems.push("tsconfig.json should exist");
  } else {
    // we can lint tsconfig here too
  }

  const operationFolderName = getLastFolder(operationFolderPath);

  if (operationFolderName !== packageJson?.name) {
    problems.push(
      "operation folder should have the same name as the package.json name"
    );
  }

  return problems;
}
