export const getCallerFileName = () => {
  const stack = new Error().stack;
  if (!stack) return;

  // console.log("Complete stack", stack);
  const firstRelevantLine = stack.split("\n")[3];
  if (!firstRelevantLine) return;

  const jsFilePath = firstRelevantLine.split("at ")[1].split(":")[0];

  if (!jsFilePath) return;

  const srcPath = jsFilePath.replace("/build/", "/src/").replace(".js", ".ts");

  return srcPath;
};

export const parseTitle = (markdown: string) => {
  const sentences = markdown
    .split("\n")
    .map((x) => x.split("."))
    .flat();

  const [title, ...rest] = sentences;

  return { title, rest: rest.filter((x) => x.length > 0).join("\n") };
};

export const log = (
  /**
   * the message to be displayed to the user
   */
  message: string,
  /**
   * the configuration (optional)
   */
  config?: LogConfig,
  /**
   * Extra data the user needs to be able to see.
   */
  data?: any
) => {
  if (config?.type === "debug" && !DEBUG) return;
  // TODO: figure out if I can also get the path, so I can easily figure out the operation and also store logs somewhere nice

  // const caller = log.caller?.name;

  // const titleParse = parseTitle(message);
  // const title = config?.title || titleParse.title;
  const color =
    config?.type === "error"
      ? options.FgRed
      : config?.type === "important"
      ? options.FgBlue
      : config?.type === "success"
      ? options.FgGreen
      : config?.type === "warning"
      ? options.FgYellow
      : null;

  if (color) {
    console.log(`${color}%s${resetString}`, message);
  } else {
    console.log(message);
  }

  if (config?.type === "error") {
    console.log(`(error happened in ${getCallerFileName()})`);
  }

  if (data) {
    if (typeof data === "object") {
      console.table(data);
    } else {
      console.log(data);
    }
  }
};

export type GlobalLogConfig = {
  showDebug?: boolean;
  showDefault?: boolean;
  sayTitles?: boolean;
  /**
   * Folder where logs can be stored. If i can access the caller location this can be the operation db.
   */
  logFolder?: string;
};
