() => {
  const stack = new Error().stack;
  if (!stack) return;

  // console.log("Complete stack", stack);
  const firstRelevantLine = stack.split("\n")[3];
  if (!firstRelevantLine) return;

  const jsFilePath = firstRelevantLine.split("at ")[1].split(":")[0];

  if (!jsFilePath) return;

  const srcPath = jsFilePath.replace("/build/", "/src/").replace(".js", ".ts");

  return srcPath;
}
