export const mdToJsonParse = (
  markdownString: string,
  fileName?: string
): MarkdownParse => {
  const { parameters, raw } = parseFrontmatterMarkdownString(markdownString);
  return {
    fileName,
    raw: markdownString,
    parameters,
    content: mdContentParseRecursively(raw, 1),
  };
};
