 async (
  outlineTitle: string,
  /**
   * content of every markdown
   */
  markdownStrings: string[],
  /**
   * filename to include in the final MarkdownParse
   */
  resultFileName?: string
): Promise<{ markdownParse: MarkdownParse; outlineString: string }> => {
  // TODO: Optionally, wrap in `details` for every docs page

  const markdownParses = markdownStrings.map((content) =>
    mdToJsonParse(content)
  );
  const { merged, outline } = mergeMarkdownParse(
    markdownParses,
    resultFileName
  );

  // BUSY HERE

  const outlineString = makeOutlineMarkdownString(outlineTitle, outline);

  return { markdownParse: merged, outlineString };
}
