 (
  simplifiedSchema: SimplifiedSchema | undefined,
  /**
   * if not given, no title is printed
   */
  name: string | undefined,
  isRequired: boolean,
  /** the headers level, defaults to 1 */
  level?: number
): string => {
  if (!simplifiedSchema) return "";

  const refLinkString = simplifiedSchema.circularRefName
    ? `[${simplifiedSchema.circularRefName}](#${getImplicitId(
        simplifiedSchema.circularRefName
      )})`
    : "";

  const enumString = simplifiedSchema.enum
    ? `(Enum: ${simplifiedSchema.enum.map(String).join(" | ")}) `
    : "";

  const optionalSubtitle = `${isRequired ? "" : " (optional)"}`;

  const nameWithoutNewlines = name ? name.replaceAll("\n", "") : undefined;
  const type = `${simplifiedSchema.type}${enumString}${refLinkString}`;
  const title = name
    ? `${"#".repeat(
        level || 1
      )} ${nameWithoutNewlines}${optionalSubtitle}: ${type}`
    : undefined;

  const arrayString = simplifiedSchema.items
    ? simplifiedSchema.items
        .map((item) => {
          const itemString = `- ${item.name}: ${item.schema.type}`;
          return itemString;
        })
        .join("\n")
    : "";

  const description = simplifiedSchema.description
    ? `> ${noNewlines(simplifiedSchema.description)}`
    : "";

  const objectString = makePropertiesTable(simplifiedSchema.properties);
  const togetherString = [title, arrayString, description, objectString]
    .filter(notEmpty)
    .join("\n\n");

  return togetherString;
}
