 (tsFunction: TsFunction): string => {
  const isComponent =
    tsFunction.operationRelativeTypescriptFilePath.endsWith(".tsx") &&
    isUpperCase(tsFunction.name.charAt(0));

  const titleString = `# ${
    isComponent ? `\`<${tsFunction.name} />\`` : `${tsFunction.name}()`
  }`;

  const infoString = `${
    tsFunction.codeSize?.lines ? `${tsFunction.codeSize.lines} LOC, ` : ""
  }Max. indexation depth: ${tsFunction.maxIndentationDepth}, ${
    tsFunction.cumulativeCodeSize?.lines
      ? `${tsFunction.cumulativeCodeSize?.lines} Cumulative LOC`
      : ""
  }`;

  const returnString = tsFunction.returnType.simplifiedSchema
    ? simplifiedSchemaToMarkdownString(
        tsFunction.returnType.simplifiedSchema,
        "Returns",
        true,
        2
      )
    : "";

  /*

| Input      |        |     |
| ---------- | ------ | --- |
| fileId     | string |     |
| **Output** |        |     |
| object     |        |     |


    */

  const paramaterTableRows =
    tsFunction.parameters && tsFunction.parameters.length > 0
      ? tsFunction.parameters.map((parameter) => {
          const summary = getJsonSchemaSummary(parameter.schema, true);
          return `| ${parameter.name}${
            parameter.required ? "" : " (optional)"
          } | ${summary?.typeDescriptor || ""} | ${
            summary?.description || ""
          } |`;
        })
      : "| - | | |";

  // const argumentStrings =
  // tsFunction.parameters && tsFunction.parameters.length > 0
  //   ? `## Parameters (${
  //       tsFunction.parameters.length
  //     })\n\n${tsFunction.parameters
  //       .map((x, i) =>
  //         simplifiedSchemaToMarkdownString(
  //           x.simplifiedSchema,
  //           `Parameter ${i + 1}: ${x.name}`,
  //           x.required,
  //           3
  //         )
  //       )
  //       .join("\n\n")}`
  //   : undefined;

  const returnTypeMarkdownString = tsFunction.returnType.typeDefinition
    ? getTypeDescriptorRecursive(tsFunction.returnType.typeDefinition, true)
    : "";

  const outputRow = `| **Output** | ${returnTypeMarkdownString}   | ${
    tsFunction.returnType.typeDefinition?.description || ""
  }   |`;
  const tableString = `
| Input      |    |    |
| ---------- | -- | -- |
${paramaterTableRows}
${outputRow}
`;

  const alineas: (string | undefined)[] = [
    titleString,
    // infoString, // TODO: Maybe make it optional.
    tsFunction.description,
    // returnTypeString,
    tableString,
  ];

  return alineas.filter(notEmpty).join("\n\n");
}
