async (
  absoluteFolderPath: string
): Promise<{ content: string; relativePath: string }[] | undefined> => {
  if (!fs.existsSync(absoluteFolderPath)) return;
  const markdownFiles = await getPublicMarkdownFilePaths(absoluteFolderPath);
  const markdownContents = await Promise.all(
    markdownFiles
      .filter((x) => fs.existsSync(x.path))
      .map(async (x) => {
        return {
          content: await fs.readFile(x.path, "utf8"),
          relativePath: makeRelative(x.path, absoluteFolderPath),
        };
      })
  );

  return markdownContents;
}
