async (
  absoluteFolderPath: string
): Promise<NestedObject<string>> => {
  const promises = (
    await getPublicMarkdownFilePaths(absoluteFolderPath, true)
  ).map(async (result) => {
    const name = getFolderJs(removeExtensionsFromPath(result.path));

    const nestedPathObjectOrPath: NestedObject<string> | string =
      result.isFolder
        ? await getPublicMarkdownNestedPathObject(result.path)
        : result.path;

    return {
      [name]: nestedPathObjectOrPath,
    };
  });

  const completeNestedObject: NestedObject<string> = mergeObjectsArray(
    await Promise.all(promises)
  );

  return completeNestedObject;
}

description:
Recursively searches a folder for public markdown files, and returns a `NestedObject` with the keys being the file or folder names, and the leafs being the absolute file paths.

File and folder names are stripped (number prefixes are removed, )

example:
```json
{
"README": "path/path/README.md"
"folder1":{
"README": "path/path/folder1/REAMDE.md"
"file1": "path/path/folder1/file1.md",
}
}
