value:
(chunk: MarkdownChunk) => {
  const childrenTitles: NestedTitle[] | undefined =
    chunk.children?.map(getTitlesRecursively);

  const returnStatement: NestedTitle[] = [];

  if (chunk.title) returnStatement.push(chunk.title);
  if (childrenTitles?.length) returnStatement.push(childrenTitles);

  return returnStatement;
}
