value:
(
  markdownChunk: MarkdownChunk
): string => {
  const title =
    markdownChunk.title && markdownChunk.level !== 0
      ? `${"#".repeat(markdownChunk.level)} ${markdownChunk.title}`
      : undefined;

  const content = markdownChunk.content;

  const children = markdownChunk.children
    ? markdownChunksToMarkdownStringRecursive(markdownChunk.children)
    : "";

  return `${title ? `${title}\n\n` : ""}${
    content ? `${content}\n\n` : ""
  }${children}`;
}
