(
  nestedTitles: NestedTitle[] | undefined,
  depth?: number
): string | undefined => {
  const realDepth = depth || 0;
  const prefix = "..".repeat(realDepth);

  const printedTogether = nestedTitles
    ?.map((nestedTitle) => {
      return Array.isArray(nestedTitle)
        ? printNestedTitles(nestedTitle, realDepth + 1)
        : `${prefix}${nestedTitle}\n`;
    })
    .join("\n");

  return printedTogether;
}
