rawText:
 async (
  markdownReaderPages: MarkdownReaderPage[],
  config?: {
    /**
     * by default, uses `markdown-reader-web`
     */
    operationName?: string;
  }
): Promise<boolean | undefined> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return;

  const nextWebFolder = await getOperationPath(
    config?.operationName || "markdown-reader-web"
  );

  if (!nextWebFolder) return;

  const referencePathsPromises = markdownReaderPages.map(async (page) => {
    if (!page.filePath) return;
    const absoluteMarkdownFilePath = path.join(projectRoot, page.filePath);
    const markdownString = await fs.readFile(absoluteMarkdownFilePath, "utf8");
    const referencePaths = getMarkdownReferencePaths(markdownString);
    return referencePaths;
  });

  const absoluteReferencePaths = (await Promise.all(referencePathsPromises))
    .filter(notEmpty)
    .flat()
    .filter(onlyUnique2());

  const projectRelativeReferencePaths = absoluteReferencePaths.map((x) =>
    makeRelative(x, projectRoot)
  );

  const publicAssetsFolder = path.join(nextWebFolder, "public");

  const removed = await removeAllExcept(publicAssetsFolder);

  const isSuccessful = await copyAllRelativeFiles(
    projectRelativeReferencePaths,
    projectRoot,
    publicAssetsFolder
  );

  return isSuccessful;
}
