 async (config?: {
  manualProjectRoot?: string;
}): Promise<undefined | MarkdownReaderPage[]> => {
  const projectRoot = config?.manualProjectRoot || getProjectRoot();
  if (!projectRoot) return;

  // the docs folder with all its files
  const docsPaths = await findAllDocsFolderPaths(true);
  const relativeDocsPages = await getMarkdownReaderPages({
    projectRoot,
    basePaths: docsPaths,
  });
  // operations
  const operationPages = await getOperationPages(projectRoot);

  // markdown model type pages
  const markdownModelTypePages = await getMarkdownModelPages(projectRoot);
  // todo pages
  const todoPages = await getTodoPages(projectRoot);

  const allPages: MarkdownReaderPage[] = [
    { queryPath: "", filePath: "README.md", isMenuItem: true },
    ...relativeDocsPages,
    ...operationPages,
    // `/dictionary` page with all words/definitions, categorised
    { queryPath: "dictionary", isMenuItem: true },
    // all markdown model types, optionally (with config)
    ...markdownModelTypePages,
    // all todo markdown folders, both in operations and outside (with config)
    ...todoPages,
  ].filter(
    onlyUnique2<MarkdownReaderPage>((a, b) => a.queryPath === b.queryPath)
  );

  return allPages;
}

