async (
  context: GetStaticPropsContext,
  basePaths: {
    projectRelativeBasePath: string;
    queryPath: string;
  }[],
  /**
   * Operation of the website that is going to be deployed
   */
  webOperationName: string
): Promise<{
  props: MarkdownReaderPageProps;
}> => {
  const flat = await docsGetPages(basePaths);
  const propsObject = await markdownReaderGetStaticPropsFromPages(
    context,
    flat,
    webOperationName
  );
  return propsObject;
}
