async (
  nestedPathObject: NestedPathObject,
  queryPath: string,
  projectRoot: string
): Promise<{
  title: string | undefined;
  description: string | null;
  descriptionProjectRelativeMarkdownPath: string | null;
  children: {
    projectRelativeMarkdownPath: string | null;
    title: string;
    firstParagraph: string | null;
    folderName: string;
  }[];
}> => {
  const childrenKeys = getKeysAtPathFromNestedObject(
    nestedPathObject,
    queryPath
  );

  const potentialReadmePath = path.join(projectRoot, queryPath, "README.md");

  const readmeMarkdownParse = fs.existsSync(potentialReadmePath)
    ? await readMarkdownFile(potentialReadmePath)
    : null;

  const queryPathTitle = queryPath.split("/").pop();
  const readmeInfo = getMarkdownIntro(readmeMarkdownParse);
  const title = readmeInfo.title || queryPathTitle;
  const description = readmeInfo.firstParagraph;

  const children = await Promise.all(
    childrenKeys.map(async (folderName) => {
      const potentialReadmePath = path.join(
        projectRoot,
        queryPath,
        folderName,
        "README.md"
      );
      const potentialOperationPath = path.join(
        projectRoot,
        queryPath,
        folderName,
        "OPERATION.md"
      );

      const pathToRead = fs.existsSync(potentialOperationPath)
        ? potentialOperationPath
        : fs.existsSync(potentialReadmePath)
        ? potentialReadmePath
        : undefined;

      const markdownParse = pathToRead
        ? await readMarkdownFile(pathToRead)
        : null;

      const { title, firstParagraph } = getMarkdownIntro(markdownParse);
      return {
        title: title || folderName,
        firstParagraph,
        folderName,
        projectRelativeMarkdownPath: pathToRead || null,
      };
    })
  );

  return {
    title,
    description,
    children,
    descriptionProjectRelativeMarkdownPath: readmeMarkdownParse
      ? potentialReadmePath
      : null,
  };
}
