async (
  projectRoot: string
): Promise<MarkdownReaderPage[]> => {
  const markdownModelNames = getObjectKeysArray(modelQueryConfig).filter(
    (modelName) => {
      const config = modelQueryConfig[modelName] as QueryConfig;

      const isMarkdownModelType = config.dbStorageMethod === "markdown";
      const noSpecificPath = !config.operationRelativePath;
      return isMarkdownModelType && noSpecificPath;
    }
  );

  const basePaths = markdownModelNames
    .map((modelName) =>
      path.join(projectRoot, "db", pluralize(kebabCase(modelName)))
    )
    .filter((potentialBasePath) => fs.existsSync(potentialBasePath));

  const markdownReaderPages = await getMarkdownReaderPages({
    projectRoot,
    basePaths,
  });

  return markdownReaderPages;
}
