async (config: {
  projectRoot: string;
  /**
   * basePaths to search for public markdown for
   */
  basePaths: string[];
  /**
   * if set, maps query path further
   */
  mapQueryPath?: (
    /**
     * already mapped the relative path to remove extension and numbers from folders
     */
    queryPath: string
  ) => string;
}): Promise<MarkdownReaderPage[]> => {
  const { basePaths, projectRoot, mapQueryPath } = config;
  const markdownReaderPages: MarkdownReaderPage[] = (
    await Promise.all(
      basePaths.map(async (basePath) => {
        const publicMarkdownFileAbsolutePaths =
          await getPublicMarkdownFilePaths(basePath, false);

        const markdownReaderPages = publicMarkdownFileAbsolutePaths.map((x) => {
          const filePath = makeRelative(x.path, projectRoot);
          const pathWithoutReadme = stripReadmeFromFolder(filePath);
          const pathWithoutExtensions =
            removeExtensionsFromPath(pathWithoutReadme);

          const queryPath = mapQueryPath
            ? mapQueryPath(pathWithoutExtensions)
            : pathWithoutExtensions;

          // NB: folders are no menu items because menu is built from queryPaths recursively
          const markdownReaderPage: MarkdownReaderPage = {
            queryPath,
            filePath,
            isMenuItem: !x.isFolder,
          };

          return markdownReaderPage;
        });

        return markdownReaderPages;
      })
    )
  ).flat();

  return markdownReaderPages;
}
