async (
  projectRoot: string,
  bundleMarkdownReaderConfig?: BundleMarkdownReaderConfig
): Promise<ReaderWebPage[]> => {
  const operationBasePaths = getPathsWithOperations({
    manualProjectRoot: projectRoot,
  });

  const operationPagesPerType: { [key: string]: ReaderWebPage[] } =
    mergeObjectsArray(
      await Promise.all(
        operationBasePaths.map(async (absoluteBasePath) => {
          const folders = (
            await exploreOperationFolders({ basePath: absoluteBasePath })
          ).map((result) => ({
            projectRelativePath: makeRelative(result, projectRoot),
          }));

          const projectRelativeBasePath = makeRelative(
            absoluteBasePath,
            projectRoot
          );

          // console.log({ folders });

          const pages: ReaderWebPage[] = folders.map((folder) => {
            const folderName = getLastFolder(folder.projectRelativePath);

            /**
             * Sometimes the bundle states the menu items should not be shown. The pages still remain available though, otherwise it would cause lots of dead links!
             */
            const isMenuHidden =
              projectRelativeBasePath === "apps" &&
              bundleMarkdownReaderConfig?.omitAppsMenu
                ? true
                : projectRelativeBasePath === "packages" &&
                  bundleMarkdownReaderConfig?.omitPackagesMenu
                ? true
                : projectRelativeBasePath === "modules" &&
                  bundleMarkdownReaderConfig?.omitModulesMenu
                ? true
                : false;

            // console.log({ isMenuItem, projectRelativeBasePath, folderName });

            const readerWebPage: ReaderWebPage = {
              queryPath: folder.projectRelativePath,
              // operation filePath is README.md
              pageData: {
                projectRelativeFilePath: path.join(
                  folder.projectRelativePath,
                  "README.md"
                ),
                internalLinkWord: folderName,
              },
              isMenuHidden,
            };

            return readerWebPage;
          });

          return { [projectRelativeBasePath]: pages };
        })
      )
    );

  const operationsPages: ReaderWebPage[] =
    bundleMarkdownReaderConfig?.customOperationNames
      ?.map((operationName) => {
        const projectRelativeOperationPath =
          operations[operationName as keyof typeof operations];

        if (!projectRelativeOperationPath) return;

        const readerWebPage: ReaderWebPage = {
          pageData: {
            projectRelativeFilePath: path.join(
              projectRelativeOperationPath,
              "README.md"
            ),
          },
          queryPath: projectRelativeOperationPath,
          isMenuHidden: false,
        };

        return readerWebPage;
      })
      .filter(notEmpty) || [];

  // NB: we need to get the right order, so it needs to be done separately
  const operationPages: ReaderWebPage[] = isBundle(projectRoot)
    ? [
        ...operationsPages,
        ...operationPagesPerType.packages,
        ...operationPagesPerType.apps,
        ...operationPagesPerType.modules,
      ]
    : [
        ...operationsPages,
        ...operationPagesPerType["operations/tools"],
        ...operationPagesPerType["operations/niches"],
      ];

  return operationPages;
}
