async (
  projectRoot: string
): Promise<MarkdownReaderPage[]> => {
  const operationsPath = getRootPath("operations");
  if (!operationsPath) return [];

  const todoFolderPaths = await findAllTodoFolderPaths(operationsPath, false);
  const markdownReaderPages: MarkdownReaderPage[] =
    await getMarkdownReaderPages({
      projectRoot,
      basePaths: todoFolderPaths,
      mapQueryPath: (queryPath) => {
        const almostPath = queryPath
          .replaceAll("todo/", "")
          .replace("operations/", "todo/");

        return almostPath.startsWith("todo/")
          ? almostPath
          : "todo/" + almostPath;
      },
    });

  return markdownReaderPages;
}
