async (
  context: GetStaticPropsContext
): Promise<{
  props: MarkdownReaderPageProps;
}> => {
  const pages = (await getAllMarkdownReaderPages()) || [];
  const projectRoot = getProjectRoot();

  if (!projectRoot) {
    return { props: { pages } };
  }

  const publicBundleConfig = await readJsonFile<
    Omit<BundleConfig, "customisableBundleConfig" | "createBundleConfig">
  >(path.join(projectRoot, "public-bundle-config.json"));

  const queryPath = getQueryPath(context.params);

  // console.log("get static props", queryPath);

  // definitions/statements/functions/interfaces/operations

  const queryPathsArray = pages
    .filter((x) => x.isMenuItem)
    .map((x) => x.queryPath);

  const nestedPathObject = queryPathsArrayToNestedPathObject(queryPathsArray);

  // NB: finds a page with actual content
  const contentPage = pages.find((x) => x.queryPath === queryPath);

  if (!contentPage) {
    // NB: we need to render an exploration page
    const {
      children,
      title,
      description,
      descriptionProjectRelativeMarkdownPath,
    } = await getFolderExplorationInfo(
      nestedPathObject,
      queryPath,
      projectRoot
    );
    return {
      props: {
        publicBundleConfig,
        pages,
        children,
        title,
        description,
        projectRelativeMarkdownPath: descriptionProjectRelativeMarkdownPath,
      },
    };
  }

  // NB: There's a content page. We need to return it (`MarkdownFile`), including its previous, next pages, and a title

  const {
    markdownFile,
    nextQueryPath,
    previousQueryPath,
    projectRelativeMarkdownPath,
  } = await getMarkdownPageInfo(
    projectRoot,
    nestedPathObject,
    queryPath,
    contentPage
  );

  const augmentedWordObject = await getAugmentedWordObject(projectRoot);

  return {
    // Passed to the page component as props
    props: {
      publicBundleConfig,
      pages,
      markdownFile,
      nextQueryPath,
      previousQueryPath,
      augmentedWordObject,
      projectRelativeMarkdownPath,
    },
  };
}
