rawText:
 (
  content: string,
  config: MarkdownParseRenderConfig
) => {
  // console.log("Need to render:", content);
  return (
    <Span>
      <ReactMarkdown
        className={config?.big ? "max-w-lg" : undefined}
        rehypePlugins={[rehypeHighlight, remarkGfm, rehypeRaw]}
        components={{
          h1: header,
          h2: header,
          h3: header,
          h4: header,
          h5: header,
          h6: header,

          img: ({ node, src, ...props }) => {
            const realSrc = getRealSrc(src, config);
            return <img src={realSrc} {...props} />;
          },
          //Fix newlines with `<br>`
          br: ({ node, ...props }: any) => (
            // @ts-ignore
            <br {...props} />
          ),

          // details: ({ node, ...props }: any) => {
          //   console.log("DETAILS DETECTED");
          //   return (
          //     // @ts-ignore
          //     <details {...props} />
          //   );
          // },

          // summary: ({ node, ...props }: any) => {
          //   console.log("SUMMARY DETECTED");
          //   return (
          //     // @ts-ignore
          //     <summary {...props} />
          //   );
          // },

          td: ({ node, children, ...props }: any) => {
            return (
              // @ts-ignore
              <td
                className="border border-gray-900 dark:border-gray-100"
                {...props}
              >
                {children?.map((child: any) =>
                  child === "<br />" ? <br /> : child
                )}
              </td>
            );
          },
          th: ({ node, ...props }: any) => (
            // @ts-ignore
            <th
              className="border border-gray-900 dark:border-gray-100"
              {...props}
            />
          ),

          ol: ({ node, ...props }: any) => (
            <ol className="list-decimal mx-3 p-2" {...props} />
          ),
          ul: ({ node, ...props }: any) => (
            <ul
              style={{ listStyleType: "circle" }}
              className="mx-3 p-2"
              {...props}
            />
          ),
          p: ({ node, ...props }: any) => (
            <p style={{ marginTop: 15, marginBottom: 15 }} {...props} />
          ),
          li: ({ node, ...props }: any) => <li className="" {...props} />,

          code: ({ node, children, ...props }: any) => {
            const [popoverShow, setPopoverShow] = React.useState(false);
            const btnRef = React.createRef<HTMLAnchorElement>();
            const popoverRef = React.createRef<HTMLSpanElement>();
            const openTooltip = () => {
              if (!btnRef.current || !popoverRef.current) return;
              createPopper(btnRef.current, popoverRef.current, {
                placement: "left",
              });
              setPopoverShow(true);
            };
            const closeTooltip = () => {
              setPopoverShow(false);
            };

            const word: React.ReactNode | string = children[0];

            const foundWord: AugmentedWord | undefined =
              typeof word === "string"
                ? config?.augmentedWordObject?.[word]
                : undefined;

            return foundWord &&
              foundWord.spoiler &&
              foundWord.spoiler.length > 0 ? (
              <span>
                <a
                  href={`/${foundWord?.queryPath}`}
                  style={{ color: "darkred" }}
                  onMouseEnter={openTooltip}
                  onMouseLeave={closeTooltip}
                  ref={btnRef}
                  {...props}
                >
                  {children}
                </a>

                <span
                  className={
                    (popoverShow ? "" : "hidden ") +
                    "bg-gray-200 border-0 mr-3 block z-50 font-normal leading-normal text-sm max-w-xs text-left no-underline break-words rounded-lg"
                  }
                  ref={popoverRef}
                >
                  <div>
                    <div className="bg-gray-200 text-black opacity-75 font-semibold px-3 mb-0 border-b border-solid border-blueGray-100 uppercase rounded-t-lg">
                      {foundWord.word}
                    </div>
                    <div className="px-3 text-gray-800">
                      {renderMarkdownContent(foundWord.spoiler, {
                        projectRelativeBaseFolderPath: getFolderJs(
                          foundWord.projectRelativeMarkdownSourcePath
                        )!,
                        projectRelativeMarkdownFilePath:
                          foundWord.projectRelativeMarkdownSourcePath,
                      })}
                    </div>
                  </div>
                </span>
              </span>
            ) : (
              <code
                className="dark:bg-gray-700"
                style={{ color: "darkcyan" }}
                {...props}
              >
                {children}
              </code>
            );
          },
          a: ({ node, href, ...props }) => {
            const youtubeId = getYoutubeId(href);
            if (youtubeId) {
              return (
                <iframe
                  width="560"
                  height="315"
                  src={`https://www.youtube.com/embed/${youtubeId}`}
                  title="YouTube video player"
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                ></iframe>
              );
            }
            return (
              <a
                className="text-blue-500"
                style={{ color: "darkblue" }}
                href={href}
                {...props}
              />
            );
          },
        }}
      >
        {content}
      </ReactMarkdown>
    </Span>
  );
}
