rawText:
 (
  content: string,
  config: MarkdownParseRenderConfig
): JSX.Element => {
  const contextSelection = useLastSelection();
  const { openContextMenuProps, renderContextMenu } = useSelectionPromptsMenu(
    config.selectionPrompts || [],
    {
      contextContent: content,
      context_projectRelativeFilePath: config.projectRelativeMarkdownFilePath,
      contextSelection,
    }
  );
  if (!content) return <Div>No content</Div>;

  const markdownRender = (
    <ReactMarkdown
      className={config?.big ? "max-w-lg" : undefined}
      rehypePlugins={[rehypeHighlight, remarkGfm, rehypeRaw]}
      components={{
        h1: HtmlHeader,
        h2: HtmlHeader,
        h3: HtmlHeader,
        h4: HtmlHeader,
        h5: HtmlHeader,
        h6: HtmlHeader,

        img: ({ src, alt }) => {
          if (!src) return <Div>Bad asset src</Div>;
          const isRelative = isPathRelative(src);
          if (isRelative && !config.projectRelativeMarkdownFilePath) {
            // We can't show the asset in this scenario
            return (
              <P>
                ![${alt}](${src})
              </P>
            );
          }

          const url = isRelative ? undefined : src;
          const relativePath = isRelative ? src : undefined;
          const asset: Asset = { alt, relativePath, absoluteUrl: url };

          return (
            <AssetView
              asset={asset}
              projectRelativeReferencingFilePath={
                config.projectRelativeMarkdownFilePath
              }
              hideDownloadLink
              className="w-96"
            />
          );
        },
        //Fix newlines with `<br>`
        br: ({ node, ...props }: any) => (
          // @ts-ignore
          <br {...props} />
        ),

        details: ({ node, ...props }) => {
          return (
            // @ts-ignore
            <details
              className="group border border-gray-600 rounded-lg"
              {...props}
            />
          );
        },

        summary: ({ node, ...props }: any) => {
          return (
            // @ts-ignore
            <summary
              className="bg-gray-200 dark:bg-gray-700 p-2 rounded-lg group-open:rounded-b-none group-open:border group-open:border-b-gray-600 select-none"
              {...props}
            />
          );
        },

        td: ({ node, children, ...props }: any) => {
          return (
            // @ts-ignore
            <td
              className="border-spacing-1 border-t border-r last:border-r-0 border-gray-600 dark:border-gray-100 p-2"
              {...props}
            >
              {children?.map((child: any) =>
                child === "<br />" ? <br /> : child
              )}
            </td>
          );
        },
        th: ({ node, ...props }) => (
          // @ts-ignore
          <th className="" {...props} />
        ),

        table: ({ node, ...props }) => (
          // @ts-ignore
          <div className="inline-block rounded-lg border shadow-2xl">
            <table className="" {...props} />
          </div>
        ),

        tbody: ({ node, ...props }) => (
          // @ts-ignore
          <tbody className="border-gray-600 dark:border-gray-100" {...props} />
        ),

        tr: ({ node, ...props }) => (
          // @ts-ignore
          <tr
            className="dark:even:bg-black even:bg-gray-400 even:bg-opacity-30"
            {...props}
          />
        ),

        ol: ({ node, ...props }: any) => (
          <ol className="list-decimal mx-3 p-2" {...props} />
        ),
        ul: ({ node, ...props }: any) => (
          <ul
            style={{ listStyleType: "circle" }}
            className="mx-3 p-2"
            {...props}
          />
        ),

        p: ({ node, ...props }) => {
          // TODO: make this work for components as well, now only works if the children of p is raw text...
          const text = makeArray(props.children)
            .map((x) => {
              if (typeof x === "string" || typeof x === "number") {
                return String(x);
              }
              return undefined;
            })
            .filter(notEmpty)
            .join("");

          const selectionResultsThisParagraph =
            config.selectionContextualPromptResults?.filter((x) =>
              x.selectionString ? text.includes(x.selectionString) : false
            );

          return (
            <>
              <p
                style={{
                  marginTop: 15,
                  marginBottom: 15,
                  whiteSpace: "pre-line",
                }}
                {...props}
              />
              {selectionResultsThisParagraph?.map((result) => {
                return (
                  <Div key={`result-${result.id}`}>
                    {result.contextualPromptSlug}: {result.resultText}{" "}
                    (selection: {result.selectionString})
                  </Div>
                );
              })}
            </>
          );
        },
        li: ({ node, ...props }: any) => <li className="" {...props} />,

        code: ({ node, children, ...props }) => {
          // NB: There's a big problem with this because all code elements get put in a `<pre>` element. I don't think this is easy to remove, but we can look in the issues. we can also look in google for how to improve it with css because i couldn't figure it out myself
          const word: React.ReactNode | string = children[0];

          const wordString: string | undefined =
            typeof word === "string" ? word : undefined;

          const foundWord: AugmentedWord | undefined = wordString
            ? config?.augmentedWordObject?.[wordString]
            : undefined;

          return foundWord &&
            foundWord.spoiler &&
            foundWord.spoiler.length > 0 ? (
            <Tooltip
              placement="left"
              tooltip={
                <>
                  <div className="font-semibold px-3 mb-0 border-b border-solid border-gray-100 rounded-t-lg">
                    {foundWord.word}
                  </div>
                  <div className="px-3">
                    {renderMarkdownContent(foundWord.spoiler, {
                      projectRelativeBaseFolderPath: getFolderJs(
                        foundWord.projectRelativeMarkdownSourcePath
                      )!,
                      projectRelativeMarkdownFilePath:
                        foundWord.projectRelativeMarkdownSourcePath,
                    })}
                  </div>
                </>
              }
            >
              <a
                href={`/${foundWord?.queryPath}`}
                style={{ color: "darkred" }}
                {...props}
              >
                {children}
              </a>
            </Tooltip>
          ) : wordString?.startsWith(".") ? (
            <Parameter text={wordString} />
          ) : (
            <code
              className="dark:bg-gray-700"
              {...props}
              style={{
                color: "darkcyan",
                // NB: the below doesn't work!
                wordBreak: "break-all",
                wordWrap: "break-word",
              }}
            >
              {children}
            </code>
          );
        },
        a: ({ node, href, ...props }) => {
          const youtubeId = getYoutubeId(href);
          if (youtubeId) {
            return (
              <iframe
                width="560"
                height="315"
                src={`https://www.youtube.com/embed/${youtubeId}`}
                title="YouTube video player"
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              ></iframe>
            );
          }
          return (
            <a
              className="dark:text-blue-200 text-blue-500"
              href={href}
              {...props}
            />
          );
        },
      }}
    >
      {content}
    </ReactMarkdown>
  );

  if (config.disableSelectionContextMenu) return markdownRender;

  // console.log("Need to render:", content);
  return (
    <div {...openContextMenuProps}>
      {renderContextMenu()}
      {markdownRender}
    </div>
  );
}

