value:
(props: { text: string }) => {
  const { text } = props;
  if (!text) return null;
  const [parameter, value] = text
    .slice(1)
    .split(":")
    .map((x) => x.trim());
  return (
    <Span className="rounded-md bg-purple-400 dark:bg-purple-800 px-1">
      {parameter ? <Span>{parameter}</Span> : null}
      {value ? <Span className="bg-white bg-opacity-50">{value}</Span> : null}
    </Span>
  );
}
