value:
(
  markdownParse: MarkdownParse,
  config: MarkdownParseRenderConfig
) => {
  // NB: not sure if this is the best way, there may be mulitiple markdown parses....
  useOpenHashDetails();

  return (
    <Div>
      {renderFrontmatter(markdownParse.parameters, { renderSpacer: true })}
      {/* NB: Previously we needed renderMarkdownTtile, renderMarkdownChunk etc... but this was not rightly parsed, so we are going to use the regular raw parser now from `react-markdown` instead... */}
      {renderMarkdownContent(markdownParse.raw, config)}
    </Div>
  );
}
