(markdownString: string): string[] => {
  const result = flattenMarkdownString(
    markdownString,
    (token) => token.type === "codespan"
  );

  const codespans: string[] = result
    .map((token) => {
      if (token.type !== "codespan") return;

      return token.text;
    })
    .filter(notEmpty);

  return codespans;
}
