value:
(
  props: MenuProps & {
    message?: string;
  }
) => {
  const {
    queryPaths,
    isLoading,
    message,
    augmentedWords,
    webPagesNested,
    webPagesFlat,
    menuHeader,
  } = props;

  const realWebPages: NestedWebPage[] =
    !webPagesNested && queryPaths
      ? getLegacyMenu(queryPaths) || []
      : webPagesNested || [];

  const [showMenu, setShowMenu] = useStore("menu.showMenu");

  useHotkey(isCtrlP, () => setShowMenu(true), []);
  useHotkey(isAltB, () => setShowMenu(!showMenu), [showMenu]);

  const [isMobileMenuEnabled, setIsMobileMenuEnabled] = useStore(
    "menu.isMobileMenuEnabled"
  );

  /**
   * This is a slow function to calculate for a lot of paths, needs to be optimised
   */

  const [search, setSearch] = useState("");
  const isSearching = search.length > 0;

  const closeMenu = (
    <Div className="lg:hidden">
      <Div>
        <ClickableIcon
          emoji="â•³"
          onClick={() => {
            setIsMobileMenuEnabled(false);
          }}
        />
      </Div>
    </Div>
  );

  return (
    <Div>
      <Div className="flex flex-row justify-between w-full">
        {menuHeader?.()}
        {closeMenu}
      </Div>

      <Input
        value={search}
        placeholder="Search"
        onChange={(e) => setSearch(e.target.value)}
        className="border px-2 border-white bg-transparent w-full rounded-lg h-9 focus:outline-none"
        autoComplete="off"
      />

      {isLoading ? (
        <FancyLoader />
      ) : isSearching && webPagesFlat ? (
        <PathSearchResults
          search={search}
          paths={webPagesFlat.map((x) => x.queryPath)}
          augmentedWords={augmentedWords}
        />
      ) : !isSearching && realWebPages ? (
        <NestedMenu items={realWebPages} headersClickable />
      ) : (
        <P>{message || "Menu.tsx: Something's wrong, no message given"}) </P>
      )}
    </Div>
  );
}
