value:
(
  props: MenuItemType & { headersClickable?: boolean; level?: number }
) => {
  const {
    title,
    children,
    icon,
    onClick,
    onDoubleClick,
    onContextMenu,
    rightIcon,
    target,
    href,
    showChildren,
    level,
    headersClickable,
  } = props;

  const router = useRouter();
  const queryPath = router.asPath.substring(1);
  const [isExpanded, toggleExpanded, setIsExpanded, setNotExpanded] =
    useExpanded(href);

  const [isMobileMenuEnabled, setIsMobileMenuEnabled] = useStore(
    "menu.isMobileMenuEnabled"
  );

  const isSelected = queryPath === href;
  const realLevel = level || 0;

  const hasChildren = children && children.length > 0 && showChildren;

  const isClickable = headersClickable || !hasChildren;
  const textClassName = isClickable
    ? `${isSelected ? "text-blue-500" : ""} hover:text-blue-800 `
    : undefined;

  const finalHref = isSelected
    ? undefined
    : isClickable
    ? !href || href.length === 0
      ? "/"
      : href
    : undefined;

  return (
    <Div className="w-full">
      <Div
        className={`w-full flex flex-row justify-center items-center pr-4 ${
          isClickable
            ? `hover:bg-gray-200 dark:hover:bg-gray-800 ${
                isSelected ? "bg-gray-200 " : ""
              }`
            : ""
        }`}
      >
        <ALink
          style={{
            paddingLeft: realLevel * 10 + 5,
            paddingBottom: 10,
            paddingTop: 10,
          }}
          className="flex flex-row justify-between"
          textClassName="flex flex-1 text-xs"
          href={finalHref}
          target={target}
          onDoubleClick={onDoubleClick}
          onContextMenu={onContextMenu}
          onClick={(e) => {
            if (isExpanded && isSelected) {
              setNotExpanded();
            } else {
              onClick?.();
              setIsExpanded();
              setIsMobileMenuEnabled(false);
            }
          }}
        >
          <Span
            //line-clamp-1 truncate text-ellipsis
            textClassName={textClassName}
          >
            {icon || ""}
            {title}
          </Span>
          <Span className="flex flex-row">
            {rightIcon ? <Span>{rightIcon}</Span> : null}
          </Span>
        </ALink>
        {hasChildren ? (
          <Div
            className="text-3xl cursor-pointer"
            onClick={() => toggleExpanded()}
          >
            {isExpanded ? "▾" : "▸"}
          </Div>
        ) : null}
      </Div>
      {hasChildren && isExpanded ? (
        <Div className="w-full">
          {children.map((child, i) => {
            return (
              <NestedMenuItem
                key={`menu-${href}-${i}`}
                {...child}
                headersClickable={headersClickable}
                level={realLevel + 1}
              />
            );
          })}
        </Div>
      ) : null}
    </Div>
  );
}
