value:
(
  /**
   * your nested path object that you want to create a menu for
   */
  nestedPathObject: NestedPathObject | null,
  /**
   * used for recursion to get the final url (defaults to empty array)
   */
  pathStack: string[] = [],
  config?: {
    target?: "_blank";
    /**
     * optionally , provide a callback function that returns the actual path
     */
    getHref?: (fullPath: string) => string;
  }
): MenuType | undefined => {
  const target = config?.target;
  const getHref = config?.getHref;

  // base case
  if (!nestedPathObject) return;

  const menu: MenuType = Object.keys(nestedPathObject).map((path) => {
    const newPathStack = pathStack.concat(path);
    const fullPath = newPathStack.join("/");
    const children = nestedPathObjectToNestedMenuRecursive(
      nestedPathObject[path],
      newPathStack,
      config
    );

    const href = getHref ? getHref(fullPath) : fullPath;

    const menuItem: MenuItemType = {
      // NB: if there is no path, the title  of the page should be "home"
      title: path.length === 0 ? "Home" : path,
      href,
      target,
      children,
      showChildren: true,
    };

    return menuItem;
  });

  return menu;
}
