export const nestedPathObjectToNestedMenuRecursive = (
  /**
   * your nested path object that you want to create a menu for
   */
  nestedPathObject: NestedPathObject | null,
  /**
   * used for recursion to get the final url (defaults to empty array)
   */
  pathStack: string[] = []
): MenuType | undefined => {
  if (!nestedPathObject) return;

  const menu: MenuType = Object.keys(nestedPathObject).map((path) => {
    const newPathStack = pathStack.concat(path);
    const fullPath = newPathStack.join("/");
    const children = nestedPathObjectToNestedMenuRecursive(
      nestedPathObject[path],
      newPathStack
    );

    const menuItem: MenuItemType = {
      title: path,
      href: fullPath,
      children,
      showChildren: true,
    };

    return menuItem;
  });

  return menu;
};
