export const recalculateOperationIndexJson = async (
  operationBasePath: string,
  manualProjectRoot?: string
) => {
  const operationMetaData = await getOperationMetaData(operationBasePath);

  if (!operationMetaData) {
    log(`Operation on ${operationBasePath} doesn't have a basepath`, {
      type: "error",
    });
    return;
  }

  const {
    operation,
    operationFolderName,
    operationName,
    relativeOperationLocationPath,
  } = operationMetaData;

  if (!operation || !operationName || !relativeOperationLocationPath) {
    log(
      `Operation on ${operationBasePath} doesn't have a name or relativeOperationLocationPath or package.json`,
      { type: "error" }
    );
    return;
  }

  const classificationIndexed = getOperationClassification(operationBasePath)!; //NB: we know it's there because operationBasePath must be valid

  const { coreDependencies, operationDependencies, packageDependencies } =
    await getDependenciesSummary(operationName);

  const categories = await categorizeFiles({
    basePath: operationBasePath,
    ignoreIndexFiles: true,
  });

  const sizeIndexed = await getFolderSummary(categories);
  // console.log("UPDATE QUERY");
  await db.update(
    "Operation",
    (item) => item.name === operation.name,
    (old) =>
      mergeNestedObject(old, {
        operation: {
          lastRebuildAt: Date.now(),
          sizeIndexed,
          folderNameIndexed: operationFolderName,
          relativeOperationLocationPathIndexed: relativeOperationLocationPath,
          classificationIndexed,
          coreDependenciesIndexed: coreDependencies,
          operationDependenciesIndexed: operationDependencies,
          packageDependenciesIndexed: packageDependencies,
        },
      }),
    { operationName: operation.name }
  );
};
