async (operationName: string) => {
  const operationImports = await db.get("TsImport", {
    operationName,
  });

  const coreDependencies = operationImports
    .filter((x) => ["node", "react"].includes(x.classification))
    .map((x) => x.name);
  const operationDependencies = operationImports
    .filter((x) => ["operation"].includes(x.classification))
    .map((x) => x.name);
  const packageDependencies = operationImports
    .filter((x) => ["package"].includes(x.classification))
    .map((x) => x.name);

  return {
    coreDependencies,
    operationDependencies,
    packageDependencies,
  };
}
