export function byteCount(s: string) {
  return encodeURI(s).split(/%..|./).length - 1;
}

export const getSizeSummary = async (
  filePath: string
): Promise<SizeSummary> => {
  const stat = await fs.stat(filePath);

  const bytes = stat.size;
  const characters = bytes;

  const bytesPerCharacter = bytes / characters;
  let lines = 1;
  const charactersPerLine = characters / lines;
  const numberOfFiles = 1;
  let linesPerFile = lines;
  if (bytes > 1024 * 1024) {
    // NB: too big to watch content

    return {
      bytes,
      lines: 0,
      characters,
      bytesPerCharacter,
      charactersPerLine,
      linesPerFile,
      numberOfFiles,
    };
  }

  const content = await fs.readFile(filePath, "utf8");
  lines = content.split("\n").length;
  linesPerFile = lines;

  return {
    bytes,
    lines,
    characters,
    bytesPerCharacter,
    charactersPerLine,
    linesPerFile,
    numberOfFiles,
  };
};

export const getFolderSummary = async (
  categorizedFiles: CategorizedFilePaths
): Promise<FolderSummary> => {
  const categoriesSummary: Omit<FolderSummary, "size"> = {
    codeSize: await sumSizeSummary(categorizedFiles.code),
    dataSize: await sumSizeSummary(categorizedFiles.data),
    textSize: await sumSizeSummary(categorizedFiles.text),
  };

  const folderSummary: FolderSummary = {
    ...categoriesSummary,
    size: sumAllKeys(Object.values(categoriesSummary), [
      "bytes",
      "characters",
      "lines",
      "numberOfFiles",
    ]),
  };

  return folderSummary;
};
