export const readJsonFile = async <T>(
  filePath: Path | undefined
): Promise<T | null> => {
  if (!filePath) return null;
  // TODO: is this needed?
  if (!fs.existsSync(filePath)) return null;

  const readable = await canRead(filePath);
  if (!readable) return null;
  const fileString = await fs.readFile(filePath, "utf8");
  if (!fileString) return null;
  const parsed = tryParseJson<T>(fileString);
  return parsed;
};

export const readJsonFileSync = <T>(filePath: Path): T | null => {
  // TODO: is this needed?
  if (!fs.existsSync(filePath)) return null;

  const readable = canReadSync(filePath);
  if (!readable) return null;
  const fileString = fs.readFileSync(filePath, "utf8");
  if (!fileString) return null;
  const parsed = tryParseJson<T>(fileString);
  return parsed;
};
