export const readKvmdFile = async (
  filePath: Path
): Promise<KeyValueMarkdownParse | null> => {
  // TODO: is this needed?
  const pathParse = path.parse(filePath);
  const isMarkdownFile = ["md", "mdx"].includes(pathParse.ext.slice(1));
  if (!isMarkdownFile) return null;
  if (!fs.existsSync(filePath)) return null;
  const readable = await canRead(filePath);
  if (!readable) return null;
  const fileString = await fs.readFile(filePath, "utf8");
  if (!fileString) return null;

  const keyValueMarkdownParse = kvMdStringToJson(fileString);
  return keyValueMarkdownParse;
};
