async (
  absoluteFilePath: string
): Promise<null | MarkdownFile> => {
  const projectRoot = getProjectRoot(absoluteFilePath);

  if (!projectRoot) return null;
  const mdParse = await readMarkdownFile(absoluteFilePath);
  if (!mdParse) return null;

  const {
    privacy,
    detectedLanguage,
    labels,
    createdAt,
    createdFirstAt,
    deletedAt,
    updatedAt,
    id,
    name,
    slug,
    cta,
    headerImage,
    headerSubtitle,
    headerTitle,
    isDraft,
    itemId,
    ...frontmatter
  } = mdParse.parameters;

  // NB: this markdown file is not always located in an operation. Yet we are trying to find it.
  const operationBasePath = findOperationBasePath(absoluteFilePath);
  const modelLocation: ModelLocation = {
    operationName: operationBasePath ? getLastFolder(operationBasePath) : null,
    projectRelativePath: makeRelative(absoluteFilePath, projectRoot),
    operationRelativePath: operationBasePath
      ? makeRelative(absoluteFilePath, operationBasePath)
      : undefined,
  };

  const markdownFile: MarkdownFile = {
    ...modelLocation,
    ...frontmatter,
    markdown: mdParse.raw,
    categoryStackCalculated: [],
    privacy: privacy as MarkdownFile["privacy"],
    detectedLanguage: detectedLanguage as MarkdownFile["detectedLanguage"],
    labels: labels as MarkdownFile["labels"],
    createdAt: createdAt as MarkdownFile["createdAt"],
    createdFirstAt: createdFirstAt as MarkdownFile["createdFirstAt"],
    deletedAt: deletedAt as MarkdownFile["deletedAt"],
    updatedAt: updatedAt as MarkdownFile["updatedAt"],
    cta: cta as MarkdownFile["cta"],
    headerImage: headerImage as MarkdownFile["headerImage"],
    headerSubTitle: headerSubtitle as MarkdownFile["headerSubTitle"],
    headerTitle: headerTitle as MarkdownFile["headerTitle"],
    isDraft: isDraft as MarkdownFile["isDraft"],
    itemId: itemId as MarkdownFile["itemId"],
    name: name as MarkdownFile["name"],
    slug: slug as MarkdownFile["slug"],
    id: id as MarkdownFile["id"],
    // NB: have to convert to unknown here first because of the specified markdown prop
  };

  return omitUndefinedValues(markdownFile);
}
