async () => {
  const argumentsArray = process.argv.slice(2);
  const currentProjectRoot = getProjectRoot();

  if (argumentsArray.length > 0) {
    oneByOne(argumentsArray, async (operationNameOrRelativePath) => {
      let operationBasePath: string | undefined;
      let realProjectRoot: string | undefined;
      if (operationNameOrRelativePath.startsWith(".")) {
        operationBasePath = path.join(
          process.cwd(),
          operationNameOrRelativePath
        );

        realProjectRoot = getProjectRoot(operationBasePath);
      } else {
        realProjectRoot = await getOperationPath(operationNameOrRelativePath);
      }

      if (!operationBasePath) {
        log("couldn't find that operation");
        return;
      }

      // console.log({ operationNames, operationName });

      const x = await rebuildOperation({
        operationBasePath,
        operationManualProjectRoot:
          realProjectRoot !== currentProjectRoot ? realProjectRoot : undefined,
        force: true,
        debug: true,
        noExit: true,
      });
    });
  }
}
