 (
  queryPaths: string[],
  initialValue: NestedPathObject
): NestedPathObject => {
  const nestedPathObject = queryPaths.reduce((nestedPathObject, queryPath) => {
    const parts: string[] = queryPath.split("/");
    const firstPart = parts[0];

    // Base case 1: if we already have it, skip it
    if (Object.keys(nestedPathObject).includes(firstPart)) {
      return nestedPathObject;
    }

    const queryPathsHere = queryPaths
      .filter((p) => p.startsWith(`${firstPart}/`))
      .map((p) => p.substring(firstPart.length + 1));

    // Base case 2: if it's a leaf (last item), and it has NO children, return it with value null
    if (parts.length === 1 && queryPathsHere.length === 0) {
      return { ...nestedPathObject, [firstPart]: null };
    }

    // Recursion case: find all queryPaths starting with the same branch
    const newNestedPathObject: NestedPathObject = {
      ...nestedPathObject,
      [firstPart]: reduceQueryPathsRecursively(queryPathsHere, {}),
    };

    return newNestedPathObject;
  }, initialValue);

  return nestedPathObject;
}
