export const say = async (input: string | SayOptions): Promise<void> => {
  const paramKeys =
    typeof input === "string"
      ? null
      : (Object.keys(input) as (keyof SayOptions)[]);

  const params =
    typeof input === "string"
      ? null
      : paramKeys?.reduce((all, key) => {
          const value = input[key];

          if (value === undefined) {
            return all;
          }

          return `${all} --${shorthands[key]}=${String(value)}`;
        }, "");

  const spawnInput = typeof input === "string" ? input : (params as string);

  spawnSync(`say ${spawnInput}`, { shell: true });
};
