 (props: {
  itemNameOrId?: string;
  parameterNameStack?: string[];
  projectRelativeStorageFilePath?: string;
  parameter: RenderableFunctionParameter;
  /**
   * In this case this must be an object or undefined
   */
  parameterValue: { [key: string]: any } | undefined;
  onChangeParameter: (newValue: any) => void;

  isDebug?: boolean;
  referencableModelData?: ReferencableModelData;
  referencedModelDataIsLoading?: boolean;

  /**
   * Unique id for this ObjectForm
   */
  id: string;
}) => {
  const {
    itemNameOrId,
    parameterNameStack,
    projectRelativeStorageFilePath,
    parameter,
    onChangeParameter,
    parameterValue,
    isDebug,
    id,
    referencableModelData,
    referencedModelDataIsLoading,
  } = props;

  /**
   * By default, the thing is expanded if the parameter is required, or if there is already a parameter value. If it is "true" by default, you can also not minimise
   */
  const defaultExpandedState = parameter.required ? true : !!parameterValue;

  const [isExpanded, setIsExpanded] = useState(defaultExpandedState);

  if (!parameter.simplifiedSchema) {
    return null;
  }

  // we can only render the object if it has properties...
  if (
    !parameter.simplifiedSchema.properties ||
    parameter.simplifiedSchema.properties.length === 0
  ) {
    // console.log("Object without properties", { parameter });
    return isDebug ? (
      <P className="text-red-500">
        Object without properties {parameter.name}{" "}
        {JSON.stringify(parameter.simplifiedSchema)}
      </P>
    ) : null;
  }

  // for each parameter, recurse this form builder

  const onChangeSubset = (objectParameterValues: any[]) => {
    // NB: every item in the array is linked with a parameter in the object
    const objectParts = parameter.simplifiedSchema?.properties?.map((x, i) => ({
      [x.name]: objectParameterValues[i],
    }));
    const objectOrNot = objectParts
      ? mergeObjectsArray(objectParts)
      : undefined;

    onChangeParameter(objectOrNot);
  };

  const valuesSubset: any[] = parameter.simplifiedSchema.properties.map(
    (x) => parameterValue?.[x.name]
  );

  const parameters: RenderableFunctionParameter[] =
    parameter.simplifiedSchema.properties.map((x) => ({
      name: x.name,
      required: x.required,
      simplifiedSchema: x.schema,
      isDbModel: parameter.isDbModel,
    }));

  // console.log({ parameters, parameter, isExpanded });

  const renderExpandButton = () => {
    if (defaultExpandedState) return null;

    return (
      <ClickableIcon
        onClick={() => setIsExpanded(!isExpanded)}
        emoji={isExpanded ? "-" : "+"}
      />
    );
  };

  const hasName = parameter.name ? parameter.name.length > 0 : false;

  return (
    <Div
      className={"ml-2 pl-2 my-4 border-l border-gray-900 dark:border-gray-100"}
    >
      {renderParameterTitle(parameter, isDebug, true, () =>
        renderExpandButton()
      )}

      {isExpanded ? (
        <SimplifiedSchemaForm
          itemNameOrId={itemNameOrId}
          parameterNameStack={
            hasName
              ? parameterNameStack
                ? [...parameterNameStack, parameter.name]
                : [parameter.name]
              : parameterNameStack
          }
          projectRelativeStorageFilePath={projectRelativeStorageFilePath}
          isDebug={isDebug}
          parameters={parameters}
          onChange={onChangeSubset}
          values={valuesSubset}
          // just passing this
          referencableModelData={referencableModelData}
          id={id}
        />
      ) : null}
    </Div>
  );
}
