value:
(props: {
  className?: string;
  onSubmit: () => void;
  title?: string;
  isLoading?: boolean;
  submitButtonText?: string;
  children?: any;
}) => {
  const { className, onSubmit, isLoading, title, children, submitButtonText } =
    props;

  return (
    <form
      className={className}
      onSubmit={(e) => {
        e.preventDefault();
        // onSubmit();
      }}
      // may be needed for file upload
      // encType="multipart/form-data"
    >
      {title ? <P className="text-3xl">{title}</P> : null}

      {children}

      <button type="submit">
        <BigButton
          isLoading={isLoading}
          onClick={onSubmit}
          title={submitButtonText || "Submit"}
        />
      </button>
    </form>
  );
}
