value:
(
  parameter: RenderableFunctionParameter,
  isDebug: boolean | undefined,
  isBolded?: boolean,
  renderAdditionalButtons?: () => JSX.Element | null
) => {
  const info = getReferenceParameterInfo(parameter.name);

  const title =
    parameter.simplifiedSchema?.title ||
    (info.isReferenceParameter
      ? humanCase(`${info.descriptor || ""} ${info.interfaceName}`)
      : humanCase(parameter.name));

  const descriptionIndicator = parameter.simplifiedSchema?.description
    ? " ℹ️"
    : "";
  return (
    <Div>
      {/* Start of a section */}
      {parameter.simplifiedSchema?.section ? (
        <P className="font-bold underline">
          {parameter.simplifiedSchema?.section}
        </P>
      ) : null}

      <Div className="w-full flex flex-row justify-between">
        <P
          data-tip={parameter.simplifiedSchema?.description}
          className={`${
            parameter.simplifiedSchema?.description
              ? "cursor-context-menu"
              : "cursor-default"
          }${isBolded ? " font-bold" : ""}`}
        >
          {title}
          {descriptionIndicator}
          {/* {parameter.required ? "(Required)" : "(Optional)"}{" "} */}
        </P>

        {parameter.renderButtons?.(parameter)}
        {renderAdditionalButtons?.()}
      </Div>
      {isDebug ? (
        <P className="italic text-gray-400">
          [{parameter.simplifiedSchema?.type}]:{" "}
          <Span className="text-gray-600">
            {parameter.simplifiedSchema?.fullComment}
          </Span>
        </P>
      ) : null}
    </Div>
  );
}
