export const objectStringToJson = (
  string: string
): { [key: string]: JSONValue } => {
  const chunks = string.split(",").map((x) => x.trim());
  const keyValues = chunks.map((chunk) => {
    const [key, value] = chunk.split(":").map((x) => x.trim());
    return { [key]: stringToJson(value) };
  });

  const mergedObject = mergeObjectsArray(keyValues);

  return mergedObject;
};
