async (operationBasePath: string) => {
  const filePaths = await getPackageSourcePaths({
    operationBasePath,
  });
  const project = getTsMorphProject(operationBasePath);
  if (!project) {
    log("couldn't load project", { type: "error" });
    return;
  }

  //select correct SourceFile from tsmorph project
  const sourceFiles = project.getSourceFiles(filePaths);

  return sourceFiles;
}
