async (
  projectRelativeFilePath: string
): Promise<{
  isSuccessful: boolean;
  message?: string;
  fileContents?: string;
}> => {
  const projectRoot = getProjectRoot();

  if (!projectRoot) {
    return { isSuccessful: false, message: "No project root" };
  }

  const absolutePath = path.join(projectRoot, projectRelativeFilePath);

  if (!fs.existsSync(absolutePath)) {
    return { isSuccessful: false, message: "File doesn't exist" };
  }

  const stats = await fs.stat(absolutePath);

  if (stats.size > 1024 * 1024 || stats.isDirectory()) {
    return {
      isSuccessful: false,
      message: "That's a directory or bigger than 1MB",
    };
  }

  const fileContents = await fs.readFile(absolutePath, "utf8");

  return { fileContents, isSuccessful: true };
}
