async (
  markdownModelName: keyof DbModels | undefined
): Promise<SimplifiedSchema | undefined> => {
  if (markdownModelName === undefined) return;

  const tsInterfaces = await db.get("TsInterface");
  const markdownModelTsInterfaces = tsInterfaces.filter(
    (x) => x.name === markdownModelName && x.isExported
  );

  // console.log({ markdownModelTsInterfaces });

  const markdownModelTsInterface = markdownModelTsInterfaces[0];

  if (!markdownModelTsInterface) return;

  const fullSimplifiedSchema = markdownModelTsInterface.type?.simplifiedSchema;

  if (!fullSimplifiedSchema) return;

  const newProperties = fullSimplifiedSchema.properties?.filter(
    (x) => !["markdown", "name"].includes(x.name)
  );

  const frontmatterSchema: SimplifiedSchema = {
    ...fullSimplifiedSchema,
    properties: newProperties,
  };

  return frontmatterSchema;
}
