async (
  projectRelativeFilePath: string
): Promise<{ isSuccessful: boolean; message?: string }> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return { isSuccessful: false, message: "No project root found" };
  }

  const absolutePath = path.join(projectRoot, projectRelativeFilePath);

  if (fs.existsSync(absolutePath)) {
    return { isSuccessful: false, message: "This file already exists" };
  }

  const folderPath = path.parse(absolutePath).dir;

  if (!fs.existsSync(folderPath)) {
    return { isSuccessful: false, message: "Folder doesn't exist" };
  }

  await fs.writeFile(absolutePath, "", { encoding: "utf8" });

  return { isSuccessful: true };
}
