async (
  projectRelativeFilePath: string,
  newFilename: string
): Promise<{ isSuccessful: boolean; message?: string }> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return { isSuccessful: false, message: "No project root found" };
  }

  const absoluteFilePath = path.join(projectRoot, projectRelativeFilePath);

  if (!fs.existsSync(absoluteFilePath)) {
    return {
      isSuccessful: false,
      message: "This file doesn't exist",
    };
  }

  const absoluteFolderPath = path.parse(absoluteFilePath).dir;

  const newAbsoluteFilePath = path.join(absoluteFolderPath, newFilename);

  if (fs.existsSync(newAbsoluteFilePath)) {
    return { isSuccessful: false, message: "File already exists" };
  }

  await fs.rename(absoluteFilePath, newAbsoluteFilePath);

  return { isSuccessful: true };
}
