rawText:
 (props: {
  markdownParse: MarkdownParse;
  augmentedWordObject?: MappedObject<AugmentedWord>;
  projectRelativeBaseFolderPath: string;
  projectRelativeMarkdownFilePath: string;
}) => {
  const {
    markdownParse,
    augmentedWordObject,
    projectRelativeBaseFolderPath,
    projectRelativeMarkdownFilePath,
  } = props;
  const [slide, setSlide] = useState(0);

  if (!markdownParse.content) return null;

  const markdownParagraphs = flattenMarkdownChunks(
    markdownParse.content
  ).filter((x) => x.paragraph !== "");

  const paragraph = markdownParagraphs[slide];

  if (!paragraph) return <P>No slides</P>;

  const navigationIcons: ClickableIconType[] = [
    {
      onClick: () => setSlide(0),
      emoji: "⏮",
      disabled: slide === 0,
    },

    {
      onClick: () => setSlide(slide - 1),
      emoji: "⬅",
      disabled: slide === 0,
    },

    {
      onClick: () => setSlide(slide + 1),
      emoji: "➡",
      disabled: slide === markdownParagraphs.length - 1,
    },

    {
      onClick: () => setSlide(markdownParagraphs.length - 1),
      emoji: "⏭",
      disabled: slide === markdownParagraphs.length - 1,
    },
  ];
  const navigation = (
    <Div className="flex flex-row gap-3">
      {navigationIcons.map((item, index) => (
        <ClickableIcon {...item} key={`icon${index}`} />
      ))}
    </Div>
  );

  return (
    <Div className="flex flex-col flex-1">
      <Div className="flex flex-1 flex-col">
        <Div className="flex flex-col justify-center items-center">
          <P className="text-4xl">
            {
              paragraph.categoryStackCalculated[
                paragraph.categoryStackCalculated.length - 1
              ]
            }
          </P>

          <Div className="flex flex-row flex-wrap">
            {renderBreadCrumbs(paragraph.categoryStackCalculated)}
          </Div>
        </Div>

        <Div className="flex flex-1 flex-col items-center justify-center overflow-y-auto">
          {renderMarkdownContent(paragraph.paragraph, {
            big: true,
            projectRelativeBaseFolderPath,
            projectRelativeMarkdownFilePath,
            augmentedWordObject,
            isDev,
            isStatic: false,
          })}
        </Div>
      </Div>

      {navigation}
    </Div>
  );
}
