rawText:
 (
  text,
  type,
  subtextConfig,
  subwordConfig,
  markdownParseRenderConfig
) => {
  if (type === "markdown" && markdownParseRenderConfig) {
    const lexed = marked.lexer(text);

    const html = lexed.map((token) => (
      <MarkedToken
        item={token}
        subtextConfig={subtextConfig}
        subwordConfig={subwordConfig}
        markdownFileConfig={markdownParseRenderConfig}
        testModeEnabled={true}
      />
    ));

    const result = <>{html}</>;

    const string = ReactDOMServer.renderToString(result);

    return string;
  }

  return text;
}
